    private DatePickerDialog picker;

    public Cake_List_Adapter(Activity activity, ArrayList<Rent_Car_Data> listd){
        this.activity = activity;
        this.dlist = listd;
        setHasStableIds(true);
    }

    @Override
    public int getItemViewType(int position) {
        return position;
    }

    @Override
    public long getItemId(int position) {
        return position;
    }

    @NonNull
    @Override
    public Cake_List_Adapter.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {

        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.adapter_rent_car,parent,false);
        return new Cake_List_Adapter.ViewHolder(v);
    }

