package com.icss.shopmax.Sub_Adapter;

import android.app.Activity;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentManager;
import androidx.fragment.app.FragmentTransaction;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.icss.shopmax.Model.Food_Menu_Model;
import com.icss.shopmax.Model.Hospital_Data;
import com.icss.shopmax.R;
import com.icss.shopmax.ui.Sub_Category.Patient_Details_Fragment;

import java.util.ArrayList;

public class Sub_Food_Menu_Adapter extends RecyclerView.Adapter<Sub_Food_Menu_Adapter.ViewHolder>
{
    ArrayList<Food_Menu_Model> dlist;
    Activity activity;

    public Sub_Food_Menu_Adapter(Activity activity, ArrayList<Food_Menu_Model> listd){
        this.activity = activity;
        this.dlist = listd;
        setHasStableIds(true);
    }

    @Override
    public int getItemViewType(int position) {
        return position;
    }

    @Override
    public long getItemId(int position) {
        return position;
    }

    @NonNull
    @Override
    public Sub_Food_Menu_Adapter.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {

        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.adapter_food_menu,parent,false);
        return new Sub_Food_Menu_Adapter.ViewHolder(v);
    }

    @Override
    public void onBindViewHolder(@NonNull final Sub_Food_Menu_Adapter.ViewHolder holder, final int i) {

        if (holder!=null){

            holder.name.setText(dlist.get(i).getFood_name());
            holder.price.setText("Rs. "+dlist.get(i).getFood_price()+"/-");
            holder.description.setText(dlist.get(i).getFood_description());
            holder.tx_qty.setText(""+dlist.get(i).getQty());


            holder.button.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    holder.button.setText("Added");
                }
            });

            holder.add.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    int num = dlist.get(i).getQty();
                    dlist.get(i).setQty(num+1);
                    notifyDataSetChanged();
                }
            });


            holder.remove.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    int num = dlist.get(i).getQty();
                    if (num<=1){}
                    else{
                        int minus = num-1;
                        dlist.get(i).setQty(minus);
                        notifyDataSetChanged();
                    }
                }
            });

            String i1 = "https://farm5.staticflickr.com/4244/34375209303_fe133a2921_o_d.png";
            String i2 = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQGyNQfS3nRwAmcULoPfKUt47K3hg-cfZX9tW2nD5vI7PrITkvuwQ&s";
            String i3 = "https://m.recipes.timesofindia.com/thumb/53314156.cms?width=600&height=600&imgsize=244848";
            String i4 = "https://www.justonecookbook.com/wp-content/uploads/2018/11/Easy-Fried-Rice-I.jpg";
            String i5 = "https://navbharattimes.indiatimes.com/hindi-recipes/wp-content/uploads/sites/2/2018/09/bread-655x477.jpg";
            String i6 = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRL5DsiH02lIsXiBPxSKbUX8d-85euSLZioAXb_S6li_79LmEoi&s";
            String i7 = "https://tiffinbuzz.in/wp-content/uploads/2018/05/corn_peas_maggiTQbuTvtwUcUm.jpg";
            String i8 = "data:image/jpeg;base64,";
            String i9 = "data:image/jpeg;base64,";

            if (i==0){
                Glide.with(activity).load(i1).into(holder.image);        }
            else if (i==1){
                Glide.with(activity).load(i2).into(holder.image);         }
            else if (i==2){
                Glide.with(activity).load(i3).into(holder.image);         }
            else if (i==3){
                Glide.with(activity).load(i4).into(holder.image);         }
            else if (i==4){
                Glide.with(activity).load(i5).into(holder.image);         }
            else if (i==5){
                Glide.with(activity).load(i6).into(holder.image);         }
            else if (i==6){
                Glide.with(activity).load(i7).into(holder.image);         }
            else if (i==7){
                Glide.with(activity).load(i8).into(holder.image);         }
            else if (i==8){
                Glide.with(activity).load(i9).into(holder.image);         }

        }
    }


    @Override
    public int getItemCount() {
        return dlist.size();
    }

    public class ViewHolder extends RecyclerView.ViewHolder
    {
        TextView name, price, description, tx_qty;
        ImageView image, add, remove;
        Button button;

        public ViewHolder(View itemview){
            super(itemview);
            name = itemview.findViewById(R.id.fd_name);
            price = itemview.findViewById(R.id.fd_price);
            description = itemview.findViewById(R.id.fd_description);
            image = itemview.findViewById(R.id.fd_img);
            button = itemview.findViewById(R.id.fd_addtocart);
            add = itemview.findViewById(R.id.fdadd_item);
            remove = itemview.findViewById(R.id.fdremove_item);
            tx_qty = itemview.findViewById(R.id.fd_qty_item);
        }
    }
}
