package com.icss.shopmax.d_Adapter;

import android.app.Activity;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentManager;
import androidx.fragment.app.FragmentTransaction;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.icss.shopmax.R;

import java.util.ArrayList;

public class It_service_Adapter extends RecyclerView.Adapter<It_service_Adapter.ViewHolder>
{
    ArrayList<String> dlist;
    Activity activity;

    public It_service_Adapter(Activity activity, ArrayList<String> listd){
        this.activity = activity;
        this.dlist = listd;
        setHasStableIds(true);
    }

    @Override
    public int getItemViewType(int position) {
        return position;
    }

    @Override
    public long getItemId(int position) {
        return position;
    }

    @NonNull
    @Override
    public It_service_Adapter.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {

        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.adapter_hospital,parent,false);
        return new It_service_Adapter.ViewHolder(v);
    }

    @Override
    public void onBindViewHolder(@NonNull It_service_Adapter.ViewHolder holder, final int i) {

        if (holder!=null){

            holder.hospital.setText(dlist.get(i));

            holder.itemView.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    AppCompatActivity activ = (AppCompatActivity) v.getContext();
                    Fragment fragment = new Sub_it_customer_service();
                    FragmentManager fragmentmanager =activ.getSupportFragmentManager();
                    FragmentTransaction fragmentTransaction =fragmentmanager.beginTransaction();
                    fragmentTransaction.addToBackStack(fragment.getTag());
                    fragmentTransaction.replace(R.id.fragment_layout,fragment);
                    fragmentTransaction.commit();
                }
            });

            String s1= "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ9ytISK482DaaC3pIoNpvBs9vxk7fnz5ddGm1OUQHcZqN_hu0P&s";
            String s2= "https://corundm.com/wp-content/uploads/2019/01/iPhone-app-development-company-ios-ios-app-ios-developer.png";
            String s3= "data:image/jpeg;base64,";
            String s4= "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS8ZwHl0fMhTbK4B5CAK0HBYWDGnPxmMdCjLN01Bti9xxS0-o5d&s";
            String s5= "data:image/jpeg;base64,";
            String s6= "https://static.javatpoint.com/operating-system/images/operating-system-tutorial.png";
            String s7= "https://www.thinkebiz.net/wp-content/uploads/2017/07/cloud-services.jpg";
            String s8= "https://usam.in/wp-content/uploads/2018/08/it-services-img.jpg";

            if (i==0){
                Glide.with(activity).load(s1).into(holder.imageView);
            }
           else if (i==1){
                Glide.with(activity).load(s2).into(holder.imageView);
            }
            else if (i==2){
                Glide.with(activity).load(s3).into(holder.imageView);
            }
            else if (i==3){
                Glide.with(activity).load(s4).into(holder.imageView);
            }
            else if (i==4){
                Glide.with(activity).load(s5).into(holder.imageView);
            }
            else if (i==5){
                Glide.with(activity).load(s6).into(holder.imageView);
            }
            else if (i==6){
                Glide.with(activity).load(s7).into(holder.imageView);
            }
            else if (i==7){
                Glide.with(activity).load(s8).into(holder.imageView);
            }

        }

    }


    @Override
    public int getItemCount() {
        return dlist.size();
    }

    public class ViewHolder extends RecyclerView.ViewHolder
    {
        TextView hospital;
        ImageView imageView;

        public ViewHolder(View itemview){
            super(itemview);
            hospital = itemview.findViewById(R.id.hs_name);
            imageView = itemview.findViewById(R.id.image);
        }
    }

}
