package com.icss.shopmax.ui.Services;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.appcompat.widget.Toolbar;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentManager;
import androidx.fragment.app.FragmentTransaction;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.icss.shopmax.Adapter.classfieds_adapter;
import com.icss.shopmax.Adapter.classfieds_quickbrowse;
import com.icss.shopmax.Adapter.classfieds_search;
import com.icss.shopmax.R;
import com.icss.shopmax.ui.MainActivity;
import com.icss.shopmax.Model.classfieds_Data;
import java.util.ArrayList;

public class classfieds_fragment extends Fragment {

    RecyclerView recyclerView;
    ImageView img1,img2;
    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_classfieds,container,false);

        Toolbar toolbar = view.findViewById(R.id.toolbar);
        toolbar.setNavigationOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                ((MainActivity)getActivity()).onBackPressed();}
        });

        recyclerView = view.findViewById(R.id.classfieds_recycler_view);
        img1 = view.findViewById(R.id.ed_search);
        img2 = view.findViewById(R.id.ed_search2);

        img1.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                Fragment fragment = new classfieds_search();
                FragmentManager fragmentmanager = getActivity().getSupportFragmentManager();
                FragmentTransaction fragmentTransaction = fragmentmanager.beginTransaction();
                fragmentTransaction.addToBackStack(fragment.getTag());
                fragmentTransaction.replace(R.id.fragment_layout, fragment);
                fragmentTransaction.commit();
            }
        });

        img2.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                Fragment fragment = new classfieds_quickbrowse();
                FragmentManager fragmentmanager = getActivity().getSupportFragmentManager();
                FragmentTransaction fragmentTransaction = fragmentmanager.beginTransaction();
                fragmentTransaction.addToBackStack(fragment.getTag());
                fragmentTransaction.replace(R.id.fragment_layout, fragment);
                fragmentTransaction.commit();
            }
        });

        pre_Data();

        return view;
    }

    private void pre_Data()
    {
        String a1="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT7pBT9Cdct5SbpuKo8CZdEuleFosFWypO0DfdzSPCgq6_uJWQvog&s";
        String a2="data:image/jpeg;base64,";
        String a3="data:image/png;base64,";
        String a4="data:image/png;base64,";
        String a5="data:image/png;base64,";
        String a6="data:image/png;base64,";

        ArrayList<classfieds_Data> dlist = new ArrayList();
        dlist.add(new classfieds_Data("Vehicles",a1));
        dlist.add(new classfieds_Data("Properties",a2));
        dlist.add(new classfieds_Data("Commercial Properties",a3));
        dlist.add(new classfieds_Data("Items",a4));
        dlist.add(new classfieds_Data("Mobile Devices",a5));
        dlist.add(new classfieds_Data("Jobs",a6));

        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(getActivity());
        recyclerView.setLayoutManager(linearLayoutManager);
        recyclerView.setAdapter(new classfieds_adapter(getActivity(),dlist));
    }
}
